-- ============================================================================
-- SCHEMA CREATION
-- ============================================================================

-- Main frameworks table
CREATE TABLE IF NOT EXISTS frameworks (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    framework_name TEXT NOT NULL,
    business_function TEXT NOT NULL,
    framework_type TEXT NOT NULL CHECK(framework_type IN ('diagnostic', 'learning')),
    sub_category TEXT,
    lifecycle_stages TEXT,
    skills_required TEXT,
    diagnostic_questions TEXT,
    red_flag_indicators TEXT,
    levers TEXT,
    current_state_assessment TEXT,
    priority_level TEXT,
    related_canon TEXT,
    notes TEXT,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- ============================================================================
-- INDEXES FOR PERFORMANCE
-- ============================================================================

CREATE INDEX IF NOT EXISTS idx_framework_name ON frameworks(framework_name);
CREATE INDEX IF NOT EXISTS idx_business_function ON frameworks(business_function);
CREATE INDEX IF NOT EXISTS idx_framework_type ON frameworks(framework_type);
CREATE INDEX IF NOT EXISTS idx_sub_category ON frameworks(sub_category);
CREATE INDEX IF NOT EXISTS idx_priority_level ON frameworks(priority_level);

-- ============================================================================
-- FULL-TEXT SEARCH (FTS5) FOR SEMANTIC SEARCH
-- ============================================================================

CREATE VIRTUAL TABLE IF NOT EXISTS frameworks_fts USING fts5(
    framework_name,
    business_function,
    sub_category,
    diagnostic_questions,
    red_flag_indicators,
    levers,
    content='frameworks',
    content_rowid='id'
);

-- Triggers to keep FTS in sync with main table
CREATE TRIGGER IF NOT EXISTS frameworks_ai AFTER INSERT ON frameworks BEGIN
    INSERT INTO frameworks_fts(rowid, framework_name, business_function, sub_category,
                                diagnostic_questions, red_flag_indicators, levers)
    VALUES (new.id, new.framework_name, new.business_function, new.sub_category,
            new.diagnostic_questions, new.red_flag_indicators, new.levers);
END;

CREATE TRIGGER IF NOT EXISTS frameworks_au AFTER UPDATE ON frameworks BEGIN
    UPDATE frameworks_fts SET
        framework_name = new.framework_name,
        business_function = new.business_function,
        sub_category = new.sub_category,
        diagnostic_questions = new.diagnostic_questions,
        red_flag_indicators = new.red_flag_indicators,
        levers = new.levers
    WHERE rowid = new.id;
END;

CREATE TRIGGER IF NOT EXISTS frameworks_ad AFTER DELETE ON frameworks BEGIN
    DELETE FROM frameworks_fts WHERE rowid = old.id;
END;

